var js2xml = require('./index');

var obj1 = {
		x: "5",
		y: [ 1, 2, 3 ],
		z: {
			z1: "10",
			z2: { z3: "15" }
		}
	};

var obj2 = {
		root: {
			$: { version: 1 },
			_: "a text string",
			list: { y: [{$:{v:2}, attr: 10}, 2, 3] }
		}
	}

console.log('Example 1\n', js2xml(obj1), "\n");
console.log('Example 2\n', js2xml(obj2), "\n");
